import React,{useEffect, useState} from 'react';
import { useNavigate } from 'react-router-dom';
export default function Component (){
    const [sqlQuery, setSqlQuery] = useState("");
    const [ruleName, setRuleName] = useState("");
    const [validationMessage, setValidationMessage] = useState("");
    const navigate = useNavigate();

    const handleSqlInputChange = (event) => {
        setSqlQuery(event.target.value);
      };
    const handleRuleNameInputChange = (event) =>{
        setRuleName(event.target.value);
    }

    useEffect(() => {
        const fetchData = async () => {
          try {
            const response = await fetch('http://localhost:5000/api/get_schemas');
            if (!response.ok) {
              throw new Error('Failed to fetch schemas');
            }

            const data = await response.json();
            setSchemas(data.schemas);
            setLoading(false);
          } catch (error) {
            console.error(error);
            setError('Error fetching schemas');
            setLoading(false);
          }
        };

        fetchData();
      }, []);

    const handleSave = async () => {
        if (sqlQuery === "" || ruleName === "") {
            setValidationMessage("<p style='color: red;'>Please enter SQL Query and Rule</p>");
        } else {
            setValidationMessage("")
            try {
                const syntaxCheckResponse = await fetch('http://localhost:5000/api/sql_query_syntax_check', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        sqlQuery: sqlQuery,
                    }),
                });

                const syntaxCheckResult = await syntaxCheckResponse.json();
                if (syntaxCheckResult.success) {
                    setValidationMessage("")
                    await fetch('http://localhost:5000/api/sql_query', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({
                            sqlQuery: sqlQuery,
                            ruleName: ruleName,
                        }),
                    });
                } else {
                    setValidationMessage(`<p style='color: red;'>${syntaxCheckResult.error}</p>`);
                }
            } catch (error) {
                console.error('Error:', error);
            }
        }
    };

    const handleCreateSchema = () => {
        navigate('/createschema')
    }

    return (
        <>
            <h1>Enter SQL query in the below Text Box</h1>
            <div>
            <textarea
                placeholder="Enter SQL Query"
                value={sqlQuery}
                onChange={handleSqlInputChange}
                className='sqlqueryinput'
            />
            </div>
            <h1>Enter the Rule Name in the below box</h1>
            <div>
                <input
                    placeholder='Enter Rule Name'
                    value={ruleName}
                    onChange={handleRuleNameInputChange}
                    className='ruleinput'
                />
            </div>
            <div dangerouslySetInnerHTML={{ __html: validationMessage }} />
            <div >
                <select className='dropdown'>
                    <option>SELECT</option>
                    <option>INSERT</option>
                    <option>DELETE</option>
                    <option>DROP</option>
                    <option>UPDATE</option>
                    <option>CREATE TABLE</option>
                </select>
            </div>
            <div>
                <button onClick={handleSave} className='saveBtn'> 
                    Save
                </button>
                <button onClick={handleCreateSchema} className='saveBtn'> 
                    Create Schema
                </button>
            </div>
        </>
    )
}
