import React,{useState} from 'react';

export default function Component (){
    const [sqlQuery, setSqlQuery] = useState("");
    const [ruleName, setRuleName] = useState("");
    const handleSqlInputChange = (event) => {
        setSqlQuery(event.target.value);
      };
    const handleRuleNameInputChange = (event) =>{
        setRuleName(event.target.value);
    }
    const handleSave = async () => {
        if (sqlQuery === "" || ruleName === "") {
            alert("Please enter SQL Query and Rule");
        } else {
            try {
                const response = await fetch('http://localhost:5000/api/sql_query', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        sqlQuery: sqlQuery,
                        ruleName: ruleName,
                    }),
                });    
                if (response.ok) {
                    const result = await response.json();
                    console.log(result);
                } else {
                    console.error('Error:', response.statusText);
                }
            } catch (error) {
                console.error('Error:', error);
            }
        }
    };

    return (
        <>
            <h1>Enter SQL query in the below Text Box</h1>
            <div>
            <textarea
                placeholder="Enter SQL Query"
                value={sqlQuery}
                onChange={handleSqlInputChange}
            />
            </div>
            <h1>Enter the Rule Name in the below box</h1>
            <div>
                <input
                    placeholder='Enter Rule Name'
                    value={ruleName}
                    onChange={handleRuleNameInputChange}
                />
            </div>
            <div>
                <button onClick={handleSave}>
                    Save
                </button>
            </div>
        </>
    )
}
